import React, { useState } from 'react';
import "./Main.css";

function Main() {
  const [number1, setNumber1] = useState("");
  const [number2, setNumber2] = useState("");
  const [team1, setTeam1] = useState("red");
  const [team2, setTeam2] = useState("red");
  const [modifier1, setModifier1] = useState("");
  const [modifier2, setModifier2] = useState("");
  const [winner, setWinner] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    const num1 = parseFloat(number1) * parseFloat(modifier1);
    const num2 = parseFloat(number2) * parseFloat(modifier2);
    const result = num1 - num2;

    if (result > 0) {
      setWinner(`Team ${team1} wins!`);
    } else if (result < 0) {
      setWinner(`Team ${team2} wins!`);
    } else {
      setWinner("It's a tie!");
    }
  };

  return (
    <div className="App">
      <h1>Team Competition</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Number 1:
            <input
              type="number"
              value={number1}
              onChange={(e) => setNumber1(e.target.value)}
              required
            />
          </label>
          <label>
            Team 1:
            <select value={team1} onChange={(e) => setTeam1(e.target.value)}>
              <option value="red">Red</option>
              <option value="green">Green</option>
              <option value="blue">Blue</option>
            </select>
          </label>
          <label>
            Modifier 1:
            <input
              type="number"
              step="0.01"
              value={modifier1}
              onChange={(e) => setModifier1(e.target.value)}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Number 2:
            <input
              type="number"
              value={number2}
              onChange={(e) => setNumber2(e.target.value)}
              required
            />
          </label>
          <label>
            Team 2:
            <select value={team2} onChange={(e) => setTeam2(e.target.value)}>
              <option value="red">Red</option>
              <option value="green">Green</option>
              <option value="blue">Blue</option>
            </select>
          </label>
          <label>
            Modifier 2:
            <input
              type="number"
              step="0.01"
              value={modifier2}
              onChange={(e) => setModifier2(e.target.value)}
              required
            />
          </label>
        </div>
        <button type="submit">Calculate Winner</button>
      </form>
      {winner && <h2>{winner}</h2>}
    </div>
  );
}

export default Main;
